// components/SavePost.js

import React, { useState, useEffect } from 'react';

function SavePost() {

  const [data, setData] = useState([]);

  // Fonction pour charger les données JSON
  const fetchData = () => {
    fetch('../json/sauvegarde.json') // Remplacez 'sauvegarde.json' par le chemin vers votre fichier JSON
      .then(response => response.json())
      .then(data => {
        setData(data); // Mettre à jour l'état avec les données chargées
      })
      .catch(error => console.error('Erreur lors du chargement des données :', error));
  };

  // Appeler la fonction fetchData lors du montage du composant
  useEffect(() => {
    fetchData();
  }, []); // Utilisation d'une dépendance vide pour s'assurer que le chargement des données ne se produit qu'une fois

  return (
  <div>
 <h2>Sauvegarde des publications</h2>
      <ul>
        {data.map((item, index) => (
          <li key={index}>
            <p>Texte: {item.texte}</p>
            <p>Image: {item.lienImage}</p>
          </li>
        ))}
      </ul>
    </div>
    );
}






export default SavePost;
