// App.js
import React, {Component} from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/Home';
import Posts from './components/Posts';

import Navbar from './components/Navbar';
import Donnees from './Donnees';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      hasError: false
    };
  }

  componentDidCatch(error, errorInfo) {
    this.setState({
      error: error,
      hasError: true
    });
  }

  render() {
    if (this.state.hasError) {
      // Vous pouvez afficher un message d'erreur personnalisé ou une page d'erreur ici
      return <div>Une erreur est survenue dans l'application.</div>;
    }

    // Le rendu habituel de votre application
    return (
      <div>
        <Router>
    <div>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/posts" element={<Posts />} />
        <Route path="/savepost" element={<SavePost />} />
        <Route path="*" element={<Home />} /> {/* Route par défaut */}
      </Routes>
      </div>
    </Router>
    <h1>Données depuis sauvegarde.json</h1>
      <Donnees />
      </div>
    );
  }
}

export default App;
