// Donnees.js
import React, { useState, useEffect } from 'react';
import donneesJSON from './sauvegarde.json';

function Donnees() {
  const [donnees, setDonnees] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Utilisation des données importées directement
    setDonnees(donneesJSON);
    setLoading(false);
  }, []);

  if (loading) {
    return <div>Chargement des données...</div>;
  }

  if (error) {
    return <div>Erreur : {error}</div>;
  }

  return (
    <div>
      {donnees.map(element => (
        <div key={element.id}>
          <h2>{element.post}</h2>
         
            
             
         <p><img src={element.urlImages[0]} alt="image" /></p>
             
           
     
          <p>Nombre de caractères : {element.number_of_characters}</p>
        </div>
      ))}
    </div>
  );
}

export default Donnees;
