import React from 'react';

class S extends React.Component {
    render() {
        const { post, urlImages, number_of_characters } = this.props.data[0];
        
        return (
            <div>
                <h1>Post</h1>
                <p>{post}</p>
                <h2>Images</h2>
                <div>
                    {urlImages.map((url, index) => (
                        <img key={index} src={url} alt={`Image ${index + 1}`} style={{ maxWidth: '100%' }} />
                    ))}
                </div>
                <p>Number of Characters: {number_of_characters}</p>
            </div>
        );
    }
}

export default SavePost;
