// src/Donnees.js
import React, { useState, useEffect } from 'react';

function Donnees() {
  const [donnees, setDonnees] = useState(null);

  useEffect(() => {
    fetch('./sauvegarde.json') // Chemin relatif au fichier JSON
      .then(response => response.json())
      .then(data => setDonnees(data[0])) // Nous supposons qu'il n'y a qu'un seul objet dans le tableau JSON
      .catch(error => console.error('Erreur lors du chargement des données :', error));
  }, []);

  if (!donnees) {
    return <div>Chargement des données...</div>;
  }

  return (
    <div>
      <h1>{donnees.post}</h1>
      <ul>
        {donnees.urlImages.map((url, index) => (
          <li key={index}>
            <img src={url} alt={`Image ${index}`} />
          </li>
        ))}
      </ul>
      <p>Nombre de caractères : {donnees.number_of_characters}</p>
    </div>
  );
}

export default Donnees;
